import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Client } from '../types/Client';
import { validateClient } from '../utils/validation';

interface EditClientFormProps {
  client?: Client;
  onUpdate?: () => void;
}

const EditClientForm: React.FC<EditClientFormProps> = ({ client: initialClient, onUpdate }) => {
    const { id } = useParams<{ id: string }>();
    const history = useHistory();
    const [client, setClient] = useState<Client | null>(initialClient || null);
    const [formData, setFormData] = useState<Client>({
        customerId: '',
        firstName: '',
        lastName: '',
        email: '',
        phoneNumber: '',
        nationalIdNumber: '',
        postalCode: '',
        dateOfBirth: '',
        streetAddress: '',
        city: '',
        country: '',
        customerType: 'Individual',
        isActive: false,
        loyaltyNumber: '',
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        console.log('EditClientForm - ID from params:', id);
        console.log('EditClientForm - Initial client:', initialClient);

        if (initialClient) {
            console.log('Using initial client');
            setClient(initialClient);
            setFormData(initialClient);
            setLoading(false);
        } else if (id) {
            // Fetch client data based on ID using your API
            const fetchClient = async () => {
                try {
                    console.log('Fetching client with customerId:', id);
                    const response = await fetch(`http://localhost:5001/clientID?ClientID=${id}`);
                    console.log('Response status:', response.status);

                    if (response.ok) {
                        const data = await response.json();
                        console.log('Fetched client data:', data);

                        if (data && (data.customerId || data.firstName)) {
                            setClient(data);
                            setFormData(data);
                            console.log('Successfully set client data');
                        } else {
                            console.log('No valid client data returned');
                            setError('Client not found');
                        }
                    } else {
                        const errorText = await response.text();
                        console.error('Error response:', errorText);
                        setError(`Failed to load client data: ${response.status}`);
                    }
                } catch (error) {
                    console.error('Fetch error:', error);
                    setError('Error fetching client data');
                } finally {
                    console.log('Setting loading to false');
                    setLoading(false);
                }
            };
            fetchClient();
        } else {
            console.log('No ID provided');
            setError('No client ID provided');
            setLoading(false);
        }
    }, [id, initialClient]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value, type } = e.target;
        const checked = (e.target as HTMLInputElement).checked;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        // Validate the form data (adapting field names for validation)
        const clientData = {
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            phoneNumber: formData.phoneNumber,
            idNumber: formData.nationalIdNumber, // Map to validation function
            postalCode: formData.postalCode,
            dateOfBirth: formData.dateOfBirth,
            city: formData.city
        };

        const validation = validateClient(clientData);

        if (!validation.isValid) {
            setError(validation.error);
            return;
        }

        setError('');

        try {
            console.log('Submitting form data:', formData);
            const response = await fetch(`http://localhost:5001/client`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                console.log('Update successful');
                if (onUpdate) {
                    onUpdate();
                } else {
                    history.push('/');
                }
            } else {
                const errorText = await response.text();
                console.error('Update failed:', errorText);
                setError('Failed to update client');
            }
        } catch (error) {
            console.error('Update error:', error);
            setError('Error updating client');
        }
    };

    console.log('Render state - loading:', loading, 'error:', error, 'client exists:', !!client);

    if (loading) {
        return (
            <div>
                <p>Loading client data...</p>
                <p>Looking for client ID: {id}</p>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ color: 'red' }}>
                <p>Error: {error}</p>
                <p>Client ID: {id}</p>
                <button onClick={() => history.push('/')}>Back to Client List</button>
            </div>
        );
    }

    if (!client) {
        return (
            <div>
                <p>Client not found</p>
                <p>ID: {id}</p>
                <button onClick={() => history.push('/')}>Back to Client List</button>
            </div>
        );
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>Edit Client</h2>
            <p>Editing: {formData.firstName} {formData.lastName}</p>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>First Name:</label>
                <input type="text" name="firstName" value={formData.firstName || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Last Name:</label>
                <input type="text" name="lastName" value={formData.lastName || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" name="email" value={formData.email || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Phone Number (optional):</label>
                <input 
                    type="tel" 
                    name="phoneNumber" 
                    value={formData.phoneNumber || ''} 
                    onChange={handleChange} 
                    placeholder="+27XXXXXXXXX or 0XXXXXXXXX"
                />
            </div>
            <div>
                <label>ID Number:</label>
                <input 
                    type="text" 
                    name="nationalIdNumber" 
                    value={formData.nationalIdNumber || ''} 
                    onChange={handleChange} 
                    required 
                    maxLength={13}
                    placeholder="13-digit South African ID"
                />
            </div>
            <div>
                <label>Street Address:</label>
                <input 
                    type="text" 
                    name="streetAddress" 
                    value={formData.streetAddress || ''} 
                    onChange={handleChange} 
                />
            </div>
            <div>
                <label>City:</label>
                <input type="text" name="city" value={formData.city || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Postal Code:</label>
                <input 
                    type="text" 
                    name="postalCode" 
                    value={formData.postalCode || ''} 
                    onChange={handleChange} 
                    required 
                    minLength={4}
                    maxLength={10}
                />
            </div>
            <div>
                <label>Country:</label>
                <input type="text" name="country" value={formData.country || ''} onChange={handleChange} />
            </div>
            <div>
                <label>Date of Birth:</label>
                <input 
                    type="date" 
                    name="dateOfBirth" 
                    value={formData.dateOfBirth ? formData.dateOfBirth.split('T')[0] : ''} 
                    onChange={handleChange} 
                    required 
                />
            </div>
            <div>
                <label>Customer Type:</label>
                <select 
                    name="customerType" 
                    value={formData.customerType || 'Individual'} 
                    onChange={handleChange} 
                    required
                >
                    <option value="Individual">Individual</option>
                    <option value="Business">Business</option>
                </select>
            </div>
            <div>
                <label>Loyalty Number:</label>
                <input type="text" name="loyaltyNumber" value={formData.loyaltyNumber || ''} onChange={handleChange} />
            </div>
            <div>
                <label>
                    <input type="checkbox" name="isActive" checked={formData.isActive || false} onChange={handleChange} />
                    Active
                </label>
            </div>
            <button type="submit">Update Client</button>
            <button type="button" onClick={() => history.push('/')}>Cancel</button>
        </form>
    );
};

export default EditClientForm;
