import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import ClientList from '../components/ClientList';
import SearchBar from '../components/SearchBar';
import Pagination from '../components/Pagination';
import { Client } from '../types/Client';

const ClientListPage: React.FC = () => {
    const history = useHistory();
    const [clients, setClients] = useState<Client[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const clientsPerPage = 10;

    // Fetch clients from API
    useEffect(() => {
        const fetchClients = async () => {
            try {
                setLoading(true);
                const response = await fetch('http://localhost:5001/client');
                if (response.ok) {
                    const data = await response.json();
                    setClients(data);
                } else {
                    setError('Failed to load clients');
                }
            } catch (error) {
                setError('Error fetching clients');
                console.error('Error fetching clients:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchClients();
    }, []);

    // Filter clients based on search term
    const filteredClients = clients.filter(client =>
        client.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        client.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        client.email.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Calculate pagination
    const indexOfLastClient = currentPage * clientsPerPage;
    const indexOfFirstClient = indexOfLastClient - clientsPerPage;
    const currentClients = filteredClients.slice(indexOfFirstClient, indexOfLastClient);
    const totalPages = Math.ceil(filteredClients.length / clientsPerPage);

    const handlePageChange = (pageNumber: number) => {
        setCurrentPage(pageNumber);
    };

    const handleEditClient = (client: Client) => {
        // Navigate to edit page using client ID
        history.push(`/edit/${client.customerId || client.id}`);
    };

    const handleDeleteClient = async (id: string) => {
        try {
            const response = await fetch(`http://localhost:5001/client?ClientID=${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                // Remove client from state
                setClients(clients.filter(client => 
                    (client.customerId || client.id) !== id
                ));
            } else {
                setError('Failed to delete client');
            }
        } catch (error) {
            setError('Error deleting client');
            console.error('Error deleting client:', error);
        }
    };

    const handleSearchChange = (term: string) => {
        setSearchTerm(term);
        setCurrentPage(1); // Reset to first page when searching
    };

    if (loading) {
        return <div>Loading clients...</div>;
    }

    return (
        <div>
            <h1>Client Management</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <SearchBar searchTerm={searchTerm} onSearchChange={handleSearchChange} />
            <ClientList 
                clients={currentClients}
                currentPage={currentPage}
                totalPages={totalPages}
                onPageChange={handlePageChange}
                onEditClient={handleEditClient}
                onDeleteClient={handleDeleteClient}
            />
            <Pagination
                currentPage={currentPage}
                totalPages={totalPages}
                onPageChange={handlePageChange}
            />
        </div>
    );
};

export default ClientListPage;
