import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Client } from '../types/Client';
import { validateClient } from '../utils/validation';

const EditClientForm: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const history = useHistory();
    const [client, setClient] = useState<Client | null>(null);
    const [formData, setFormData] = useState<Client>({
        customerId: '',
        firstName: '',
        lastName: '',
        email: '',
        phoneNumber: '',
        nationalIdNumber: '',
        postalCode: '',
        dateOfBirth: '',
        streetAddress: '',
        city: '',
        country: '',
        customerType: '',
        isActive: false,
        loyaltyNumber: '',
    });
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch client data based on ID using your API
        const fetchClient = async () => {
            try {
                const response = await fetch(`http://localhost:5001/clientID?ClientID=${id}`);
                if (response.ok) {
                    const data = await response.json();
                    setClient(data);
                    setFormData(data);
                } else {
                    setError('Failed to load client data');
                }
            } catch (error) {
                setError('Error fetching client data');
            }
        };
        fetchClient();
    }, [id]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        // Validate the form data (adapting field names for validation)
        const clientData = {
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            phoneNumber: formData.phoneNumber,
            idNumber: formData.nationalIdNumber, // Map to validation function
            postalCode: formData.postalCode,
            dateOfBirth: formData.dateOfBirth,
            city: formData.city
        };

        const validation = validateClient(clientData);

        if (!validation.isValid) {
            setError(validation.error);
            return;
        }

        setError('');

        try {
            const response = await fetch(`http://localhost:5001/client`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                history.push('/clients');
            } else {
                setError('Failed to update client');
            }
        } catch (error) {
            setError('Error updating client');
        }
    };

    if (!client) {
        return <div>Loading...</div>;
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>Edit Client</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>First Name:</label>
                <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
            </div>
            <div>
                <label>Last Name:</label>
                <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} required />
            </div>
            <div>
                <label>Phone Number (optional):</label>
                <input 
                    type="tel" 
                    name="phoneNumber" 
                    value={formData.phoneNumber} 
                    onChange={handleChange} 
                    placeholder="+27XXXXXXXXX or 0XXXXXXXXX"
                />
            </div>
            <div>
                <label>ID Number:</label>
                <input 
                    type="text" 
                    name="nationalIdNumber" 
                    value={formData.nationalIdNumber} 
                    onChange={handleChange} 
                    required 
                    maxLength={13}
                    placeholder="13-digit South African ID"
                />
            </div>
            <div>
                <label>Street Address:</label>
                <input 
                    type="text" 
                    name="streetAddress" 
                    value={formData.streetAddress} 
                    onChange={handleChange} 
                />
            </div>
            <div>
                <label>City:</label>
                <input type="text" name="city" value={formData.city} onChange={handleChange} required />
            </div>
            <div>
                <label>Postal Code:</label>
                <input 
                    type="text" 
                    name="postalCode" 
                    value={formData.postalCode} 
                    onChange={handleChange} 
                    required 
                    minLength={4}
                    maxLength={10}
                />
            </div>
            <div>
                <label>Country:</label>
                <input type="text" name="country" value={formData.country} onChange={handleChange} />
            </div>
            <div>
                <label>Date of Birth:</label>
                <input 
                    type="date" 
                    name="dateOfBirth" 
                    value={formData.dateOfBirth ? formData.dateOfBirth.split('T')[0] : ''} 
                    onChange={handleChange} 
                    required 
                />
            </div>
            <div>
                <label>Customer Type:</label>
                <input type="text" name="customerType" value={formData.customerType} onChange={handleChange} />
            </div>
            <div>
                <label>Loyalty Number:</label>
                <input type="text" name="loyaltyNumber" value={formData.loyaltyNumber} onChange={handleChange} />
            </div>
            <div>
                <label>
                    <input type="checkbox" name="isActive" checked={formData.isActive} onChange={handleChange} />
                    Active
                </label>
            </div>
            <button type="submit">Update Client</button>
        </form>
    );
};

export default EditClientForm;
