import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Client } from '../types/Client';
import { validateClient } from '../utils/validation';

interface EditClientFormProps {
  client?: Client;
  onUpdate?: () => void;
}

const EditClientForm: React.FC<EditClientFormProps> = ({ client: initialClient, onUpdate }) => {
    const { id } = useParams<{ id: string }>();
    const history = useHistory();
    const [formData, setFormData] = useState<Client>({
        customerId: '',
        firstName: '',
        lastName: '',
        email: '',
        phoneNumber: '',
        nationalIdNumber: '',
        postalCode: '',
        dateOfBirth: '',
        streetAddress: '',
        city: '',
        country: '',
        customerType: 'Individual',
        isActive: false,
        loyaltyNumber: '',
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (initialClient) {
            setFormData(initialClient);
            setLoading(false);
        } else if (id) {
            fetch(`http://localhost:5001/clientID?ClientID=${id}`)
                .then(response => {
                    if (!response.ok) {
                        throw new Error(`HTTP ${response.status}`);
                    }
                    return response.text(); // Use text() instead of json()
                })
                .then(text => {
                    const data = JSON.parse(text); // Parse manually
                    setFormData(data);
                    setLoading(false);
                })
                .catch(error => {
                    console.error('Error:', error);
                    setError('Failed to load client');
                    setLoading(false);
                });
        } else {
            setLoading(false);
        }
    }, [id, initialClient]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value, type } = e.target;
        const checked = (e.target as HTMLInputElement).checked;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        const clientData = {
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            phoneNumber: formData.phoneNumber,
            idNumber: formData.nationalIdNumber,
            postalCode: formData.postalCode,
            dateOfBirth: formData.dateOfBirth,
            city: formData.city
        };

        const validation = validateClient(clientData);

        if (!validation.isValid) {
            setError(validation.error);
            return;
        }

        try {
            const response = await fetch(`http://localhost:5001/client`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                history.push('/');
            } else {
                setError('Failed to update client');
            }
        } catch (error) {
            setError('Error updating client');
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return (
            <div style={{ color: 'red' }}>
                <p>Error: {error}</p>
                <button onClick={() => history.push('/')}>Back to Client List</button>
            </div>
        );
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>Edit Client</h2>
            <div>
                <label>First Name:</label>
                <input type="text" name="firstName" value={formData.firstName || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Last Name:</label>
                <input type="text" name="lastName" value={formData.lastName || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" name="email" value={formData.email || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Phone Number:</label>
                <input type="tel" name="phoneNumber" value={formData.phoneNumber || ''} onChange={handleChange} />
            </div>
            <div>
                <label>ID Number:</label>
                <input type="text" name="nationalIdNumber" value={formData.nationalIdNumber || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Street Address:</label>
                <input type="text" name="streetAddress" value={formData.streetAddress || ''} onChange={handleChange} />
            </div>
            <div>
                <label>City:</label>
                <input type="text" name="city" value={formData.city || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Postal Code:</label>
                <input type="text" name="postalCode" value={formData.postalCode || ''} onChange={handleChange} required />
            </div>
            <div>
                <label>Country:</label>
                <input type="text" name="country" value={formData.country || ''} onChange={handleChange} />
            </div>
            <div>
                <label>Date of Birth:</label>
                <input 
                    type="date" 
                    name="dateOfBirth" 
                    value={formData.dateOfBirth ? formData.dateOfBirth.split('T')[0] : ''} 
                    onChange={handleChange} 
                    required 
                />
            </div>
            <div>
                <label>Customer Type:</label>
                <select name="customerType" value={formData.customerType || 'Individual'} onChange={handleChange} required>
                    <option value="Individual">Individual</option>
                    <option value="Business">Business</option>
                </select>
            </div>
            <div>
                <label>Loyalty Number:</label>
                <input type="text" name="loyaltyNumber" value={formData.loyaltyNumber || ''} onChange={handleChange} />
            </div>
            <div>
                <label>
                    <input type="checkbox" name="isActive" checked={formData.isActive || false} onChange={handleChange} />
                    Active
                </label>
            </div>
            <button type="submit">Update Client</button>
            <button type="button" onClick={() => history.push('/')}>Cancel</button>
        </form>
    );
};

export default EditClientForm;
